import React, {useState, useEffect} from 'react'

export default function Game(props) {
    const [selected, setSelected] = useState([])

    let data = props.data
    let score = 0;

    const saveSelection = (ID) => () => { 
        score =+ 1
        setSelected(prevSelected => [ID, ...prevSelected])
        selectionChecker(ID)
        reset()
        console.log(score)
    }

    function reset() {
        data = props.randomize(data)
    }

    function selectionChecker(ID) {
        for (let i = 0; i <= selected.length; i++){
            if (ID === selected[i]){
                score = 0;
            }
        }
    }

    useEffect(() => {
    }, [selected])

    return (
        <div className='cardWrapper'>
            <h1>{score}</h1>
            <div onClick={saveSelection(data[0].ID)}>
                <img src={data[0].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[1].ID)}>
                <img src={data[1].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[2].ID)}>
                <img src={data[2].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[3].ID)}>
                <img src={data[3].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[4].ID)}>
                <img src={data[4].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[5].ID)}>
                <img src={data[5].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[6].ID)}>
                <img src={data[6].imgPath} alt=''/>
            </div>
            <div onClick={saveSelection(data[7].ID)}>
                <img src={data[7].imgPath} alt=''/>
            </div>
        </div>
    )
}
